import React, { Component, Fragment } from 'react';
import {Route, Switch, BrowserRouter} from 'react-router-dom';
import HomePage from '../HomePage/HomePage';
import AdoptPage from '../AdoptPage/AdoptPage';
import ErrorPage from '../ErrorPage/ErrorPage';
import UserContext from '../../contexts/UserContext';
import apiService from '../../services/apiService';
import { cats } from '../../../../petful-server-template/store';

const Queue = require('../../queue/Queue');

export class Root extends Component {

  state = {
    cats: new Queue(),
    dogs: new Queue(),
    people: new Queue(),
  }

  componentDidMount() {
    apiService.getPets()
      .then(data => {
        let cData = data.cats.first;
        this.setState(this.state.cats.enqueue(cData.data))
        let nc = cData.next.data;
        while(nc) {
          this.setState(this.state.cats.enqueue(n))
          nc = nc.next.data
        }
        let dData = data.dogs.first;
        this.setState(this.state.dogs.enqueue(dData.data))
        let nd = dData.next.data;
        while(nd) {
          this.setState(this.state.dogs.enqueue(n))
          nd = nd.next.data
        }
      })

    apiService.getPeople()
      .then(data => {
        console.log(data);
      })

  }

  render() {
    return (
      <UserContext.Provider value = {{
        pets: this.state.pets,
        people: this.state.people,
      }}>
        <Fragment>
          <main>
          <BrowserRouter>
          <ErrorPage>
            <Switch>
              <Route
                exact path = {'/'}
                component = {HomePage}/>
              <Route
                exact path = {'/adopt'}
                component = {AdoptPage}/>
            </Switch>
            </ErrorPage>
          </BrowserRouter>
          </main>
        </Fragment>
      </UserContext.Provider>
    );
  };
};

export default Root
